/* Reset & standardize default styles */
@import url("https://unpkg.com/@primo-app/primo@1.3.64/reset.css") layer;

/* Design tokens (apply to components) */
:root {
  --color-primary: #9b8580;
  --color-primary-light: #e9e2e2;
  --color-accent: #6371a2;
  --color-dark: #6c6e83;
  --color-light: linear-gradient(to bottom, #76a6b5 0%, #ffffff 60%, #cccccc 85%, rgba(0, 0, 0, 0.5) 100%);

  /* Base values */
  --box-shadow: 0px 4px 30px rgba(0, 0, 0, 0.2);
  --border-radius: 0;
  --border-color: #e0e1e1;
  --transition: 0.1s ease-in-out;
}

html {
  font-size: 14px;
  background: var(--color-light);
}

@media (max-width: 600px) {
  html {
    font-size: 13px; /* Daha iyi uyum için */
  }
}

@media (max-width: 440px) {
  html {
    font-size: 12px;
  }
}

/* Root element (use instead of body) */
#page {
  font-family: 'Merriweather', serif;
  color: #111;
  line-height: 1.5;
  font-size: 1.125rem;
  background: var(--color-light);
  max-width: 100%; /* Güncellendi: Tüm genişlikte kullanılabilir */
  margin: auto;
  padding: 1rem;
}

/* Elements */
.section-container {
  max-width: 100%; /* Güncellendi: Tüm genişlikte kullanılabilir */
  margin: 0 auto;
  padding: 1rem;
}

a.link {
  line-height: 1.3;
  border-bottom: 2px solid var(--color-accent);
  transform: translateY(-2px);
  transition: border var(--transition);

  &:hover {
    border-color: transparent;
  }
}

.heading {
  font-size: 2.5rem;
  line-height: 1.15;
}

/* Buttons */
.button {
  color: white;
  background: var(--color-dark);
  border-radius: 8px;
  padding: 9px 16px;
  transition: box-shadow var(--transition);
  border: 0; /* reset */

  @media (max-width: 600px) {
    padding: 6px 12px; /* Küçük ekranlarda düğme içeriğini ayarlamak için */
  }

  &:hover {
    box-shadow: 0 0 0 2px var(--color-accent);
  }

  &.inverted {
    background: transparent;
    color: var(--color-accent);
    border: 2px solid var(--color-accent);
  }
}

/* Headings */
h1 {
  font-size: 2rem;
  font-weight: 500;
  line-height: 1.1;
  margin-bottom: 1.5rem;
}

h2 {
  font-size: 2.5rem;
  font-weight: 500;
  margin-bottom: 1rem;
  text-align: center;
}

h3 {
  font-size: 1.3rem;
  font-weight: 500;
  margin-bottom: 1rem;
}

h4 {
  font-size: 1.2rem;
  font-weight: 500;
  margin-bottom: 1rem;
}

/* Content Section */
.content {
  max-width: 100%; /* Güncellendi: Tüm genişlikte kullanılabilir */
  margin: 0 auto;
  padding: 2rem 1rem;

  p {
    margin-bottom: 1rem;
    line-height: 1.5;
    text-align: justify;
  }

  img {
    width: 100%;
    margin: 2rem 0;
    box-shadow: var(--box-shadow);
    border-radius: var(--border-radius);
  }

  a.link {
    font-weight: 500;
    border-bottom: 2px solid var(--color-accent);
    transform: translateY(-2px);
    transition: border var(--transition);

    &:hover {
      border-color: transparent;
    }
  }

  h1 {
    font-size: 3.2rem;
    font-weight: 600;
    line-height: 1.1;
    margin-bottom: 1.5rem;
  }

  h2 {
    font-size: 1.8rem;
    font-weight: 600;
    margin-bottom: 1rem;
  }

  h3 {
    font-size: 1.5rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  h4 {
    font-size: 1.3rem;
    font-weight: 500;
    margin-bottom: 1rem;
  }

  ul {
    list-style: disc;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  ol {
    list-style: decimal;
    padding: 0.5rem 0;
    padding-left: 1.25rem;
  }

  blockquote {
    padding: 2rem;
    margin: 1.5rem 0;
    border-left: 5px solid var(--color-accent);
  }
}

/* Flex utility */
.flex {
  display: flex;
  flex-wrap: wrap; /* Mobil uyumlu olması için */
}

:global(iframe) {
  margin: auto;
  margin-bottom: 4rem;
}

/* Genel Ayarlar */
.section-container {
  padding: 2rem;
}

.teasers {
  display: grid;
  gap: 2rem;
}

.teaser {
  display: grid;
  gap: 1rem;
}

/* Resim üstte, metin altta */
.teaser.text_under_image {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
}

.teaser.text_under_image img {
  width: 100%;
  height: auto;
  max-width: 900px;
  min-width: 300px;
  object-fit: contain;
  margin-bottom: 1rem;
}

.teaser.text_under_image .body {
  width: 80%;
  text-align: justify;
  padding: 0.5rem 0;
}

/* Görsel sağda veya solda, metin yanında */
.teaser.text_image_right, .teaser.text_image_left {
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}

.teaser.text_image_right .image-right {
  order: 1;
  margin-left: 1rem;
  width: 50%;
}

.teaser.text_image_left .image-left {
  order: 0;
  margin-right: 1rem;
  width: 50%;
}

.teaser.text_image_right img, .teaser.text_image_left img {
  width: 100%;
  height: auto;
  object-fit: cover;
}

.teaser.text_image_right .body, .teaser.text_image_left .body {
  width: 50%;
  min-height: 300px;
  text-align: justify;
  padding: 1rem;
  box-sizing: border-box;
}

/* Görsel sağa veya sola dayalı, metin etrafını sarar */
.teaser.float_right, .teaser.float_left {
  display: flex;
  align-items: flex-start;
  width: 100%;
}

.teaser.float_right .image-right {
  float: right;
  margin-left: 1rem;
}

.teaser.float_left .image-left {
  float: left;
  margin-right: 1rem;
}

.teaser.float_right img, .teaser.float_left img {
  max-width: 50%;
  height: auto;
  float: inherit;
}

.teaser.float_right .body, .teaser.float_left .body {
  margin: 0;
  padding: 1rem;
  overflow: hidden;
}

/* Görsel yok, sadece yazı */
.teaser.only_text .body {
  width: 80%;
  text-align: justify;
  padding: 0.5rem 0;
}

/* Genel title, description ve link ayarları */
.teaser .title {
  font-size: 2rem;
  font-weight: 600;
  text-align: left;
}

.teaser .description {
  line-height: 1.5;
  text-align: justify;
}

.teaser .link {
  text-align: center;
  color: var(--color-accent);
  font-weight: 500;
  border-bottom: 2px solid var(--color-accent);
}

