import Fuse from 'https://cdn.jsdelivr.net/npm/fuse.js@7.1.0/dist/fuse.mjs'

let hovered_link = null;
let hovered_link_container = null;
let mobileNavOpen = false 
let currentLang = 'tr';
const sitemap_en = [
