{ "title": "Başarı", "url": "success" },
];

let searchResults = [];

$: otherLang = currentLang == 'tr' ? 'en' : 'tr';

function handleMouseout(e) {
if(!e.composedPath().includes(hovered_link_container)) { 
    hovered_link = null; 
  }
}

onMount(() => {
  const currentUrl = new URL(window.location.href);
  currentLang = currentUrl.pathname.split('/')[1] == 'en' ? 'en' : 'tr';

  /* 
    fetch('/sitemap.xml').then(res => res.text()).then(xml => {

    });
  */
});

function langChangeHandler(selectedLang) {
 const currentUrl = new URL(window.location.href);

  let newUrl = new URL(window.location.href);
  if(selectedLang == 'en') {
    if (currentUrl.pathname.split('/')[1] !== 'en') {
      newUrl.pathname = `/en${currentUrl.pathname}`;
    }
  } else {
    if(currentUrl.pathname.split('/')[1] === 'en') {
      newUrl.pathname = currentUrl.pathname.split('/').filter((e,i) => i != 1).join('/')
    }
  }

  if(selectedLang == 'it') {
    if (currentUrl.pathname.split('/')[1] !== 'it') {
      newUrl.pathname = `/it${currentUrl.pathname}`;
    }
  } else {
    if(currentUrl.pathname.split('/')[1] === 'it') {
      newUrl.pathname = currentUrl.pathname.split('/').filter((e,i) => i != 1).join('/')
    }
  }

  if(currentUrl.href != newUrl.href) {
    window.location.href = newUrl.href;
  }
}

function toggleLanguage(e) {
  const selectedLang = e.currentTarget.value;
  langChangeHandler(selectedLang);
}

function toggleMobileLangChange() {
  langChangeHandler(otherLang);
}
function toggleMobileNav() {
  mobileNavOpen = !mobileNavOpen
}


const debounce = function(func, wait, immediate) {
  var timeout;
  return function() {
    var context = this, args = arguments;
    var later = function() {
      timeout = null;
      if (!immediate) func.apply(context, args);
    };
    if (immediate && !timeout) func.apply(context, args);
    clearTimeout(timeout);
    timeout = setTimeout(later, wait);
  };
};


let searchQuery = '';

const fuse = new Fuse(currentLang == 'en' ? sitemap_en : currentLang == 'it' ? sitemap_it : sitemap, {
  keys: ['title']
});

function _search() {
  searchResults = fuse.search(searchQuery);
}

const search = debounce(_search, 500);

$: if(searchQuery) {
  search();
} else {
  searchResults = [];
}

